                    <input
                        value={password}
                        onChange={(e) => {setpassword(e.target.value)}}
                        className='form-control mt-4'
                        placeholder='Introduce la contraseÃ±a'
                        type='password'/>

                    <input
                        className='btn btn-block btn-dark mt-4'
                        placeholder='Sign in'
                        type='submit'/>
                </form> */}
                {
                    redirect !== false ?
                    (
                        <Redirect to='/products'/>
                    ):
                    (
                        <span></span>
                    )
                }
            </div>
            <div className='col'></div>
        </div>
    )
}

